		<?if($this->uri->segment(2) == "edit"):?>			$(function(){				var btnUpload=$('#upload');				var status=$('#status');				new AjaxUpload(btnUpload, {					action: '<?= base_url()?>uploadfile/do_upload/<?= $dafkomen[0]->id?>',					name: 'xfile',					onSubmit: function(file, ext){						 if (! (ext && /^(jpg|png|jpeg|gif)$/.test(ext))){ 							// extension is not allowed 							status.text('Only JPG, PNG or GIF files are allowed');							return false;						}						status.text('Uploading...');					},					onComplete: function(file, response){						//On completion clear the status						status.text('');						//Add uploaded file to list						//alert(response);						if(response.search("Success")>-1){							x= response.split(";");							console.log(response);							console.log(x);							console.log(x[1]);							gambar.push(x[1]);							$("#igambar").val(gambar);							alertify.alert('Pemindahan File Berhasil Harap klik button untuk konfimasi file yang di upload anda');						}else{							console.log(response);							//alert('Gagal Upload File');						}										}				});			});		<?endif?>
		function validate_number_integer_only(evt) {			  var theEvent = evt || window.event;			  var key = theEvent.keyCode || theEvent.which;			  key = String.fromCharCode( key );			  var regex = /[0-9]|[\b]|\t/;			  if (key.keyCode != 8)				  if( !regex.test(key)) {					theEvent.returnValue = false;					if(theEvent.preventDefault) theEvent.preventDefault();				  }		}		
		function before_save(){			if($("#id_merk").val() == null){				alertify.alert("Pilih kategori dahulu");							return false;			}						if($("#id_kategori").val() == null){				alertify.alert("Pilih kategori dahulu");							return false;			}
			if($("#name_barang").val() == ""){				$("#name_barang").focus();				alertify.alert("Masukkan nama barang dahulu");							return false;			}
			if($("#seo").val() == ""){				$("#seo").focus();				alertify.alert("Masukkan seo dahulu");							return false;			}
			if($("#harga").val() == ""){				$("#harga").val("0");				$("#harga").focus();				alertify.alert("Masukkan harga dahulu");							return false;			}
			if($("#diskon").val() == ""){				$("#diskon").val("0");				$("#diskon").focus();				alertify.alert("Masukkan diskon dahulu");							return false;			}
			if($("#berat").val() == ""){				$("#berat").val("0");				$("#berat").focus();				alertify.alert("Masukkan berat dahulu");							return false;			}
			if($("#kategori").val() == ""){				$("#kategori").focus();				alertify.alert("Masukkan kategori dahulu");							return false;			}
			<?if($this->uri->segment(2) != "edit"){?>				if($("#kode").val() == ""){					$("#kode").focus();					alertify.alert("Masukkan kode dahulu");								return false;				}				<?}?>					$("#form_kategori").submit();						}
		
