<script>	var gambar=new Array();	tinymce.init({		selector: "textarea",		plugins: ["code", "image"]	});
	function validate_number_integer_only(evt) {		  var theEvent = evt || window.event;		  var key = theEvent.keyCode || theEvent.which;		  key = String.fromCharCode( key );		  var regex = /[0-9]|[\b]|\t/;		  if (key.keyCode != 8)			  if( !regex.test(key)) {				theEvent.returnValue = false;				if(theEvent.preventDefault) theEvent.preventDefault();			  }	}			function add_row_information(){		var stock = $("#input_stock").val();		var price = $("#input_price").val();				if($("#table_stock thead input[value=none]").length > 0){			alert("You can not input another size after input none");			return false;		}				if($("#table_stock thead tr").length > 1 && $("#input_information").val() == 'none'){			alert("You can not input none size after input another size");			return false;		}				if($("#table_stock thead input[value="+$("#input_information").val()+"]").length > 0){			alert("You can not input stock with same size");			return false;		}else if(stock.trim() == ""){			alert("Please input stock first");			return false;		}else if(price.trim() == ""){			alert("Please input price first");			return false;		}					var string = "<tr><td class='form-group-sm text-center'>"+$("#input_information").val()+"<input type='hidden' value='"+$("#input_information").val()+"' name='information[]'></td><td class='form-group-sm'><input value='"+$("#input_stock").val()+"' type='text' name='stock[]' class='form-control'></td><td><input value='"+$("#input_price").val()+"' type='text' name='price[]' class='form-control'></td><td style='vertical-align:middle;text-align:center;'><a href='javascript:void(0)' onclick='delete_row_infomation($(this));' class='btn btn-danger btn-xs'>Delete</a></td></tr>";		$("#table_stock thead").append(string);		$("#input_stock").val("");		$("#input_price").val("");	}		function delete_row_infomation(obj){		obj.closest("tr").remove();	}</script>

